function get_projects(){$.ajax({type:"POST",url:"database.php",data:{action:"get"}}).always(function(e){console.log("got",e);$("#projects").html("hello")})}function add_project(){var e=$("#projectName").val(),t=$("#participants").val(),n=$("#description").val();$.ajax({type:"POST",url:"database.php",data:{action:"add",name:e,participants:t,description:n}}).always(function(e){console.log("added",e);get_projects()})}$(document).ready(function(){get_projects();$("#submit").on("click",function(){add_project()})});