function get_projects(){$.ajax({type:"POST",url:"database.php",data:"action=get",success:function(e){alert(e)}})}function add_project(){var e=$("#projectName").val(),t=$("#participants").val(),n=$("#description").val();$.ajax({type:"POST",url:"database.php",data:"action=add&name="+e+"&participants="+t+"&description="+n,success:function(e){alert(e);get_projects()}})}$(document).ready(function(){get_projects();$("#addProject").click(function(e){e.preventDefault();add_project()})});